import React, { useEffect, useState } from "react"
import { Row, Col, Image } from "antd"
import "./details.less"
import dayjs from "dayjs"
import { useParams, useNavigate } from "react-router-dom"
import api from "@/api"
import { getItem } from "@/utils"
import IconFont from "../../component/IconFont"
function Details() {
  const params = useParams()
  const navigate = useNavigate()
  const [details, setDetails] = useState({
    title: null,
    updatetime: null,
    content: null,
  })
  const init = async () => {
    let fn
    if (params.type === "article") {
      fn = "getAllArticle"
      const data = await api.common[fn](params.id)
      if (data) {
          setDetails(data)
      }
     
    } else {
      fn = "getNewsDetails"
      const { code, data } = await api.common[fn]({ id: params.id })
      if (code === 1) {
        setDetails(data)
      }
    }
  }
  useEffect(() => {
    document.onselectstart = function(){
      return false;
    }
    init()
  }, [])
  return (
    <div className="news-details-page">
      <IconFont
        onClick={() => navigate(-1)}
        type="icon-fanhui"
        className="m-b-20"
        size={26}
      />
      <Row>
        <Col span={24} className="m-b-20">
          <h2>{details.title}</h2>
        </Col>
        <Col span={24} className="m-b-20">
          <Row justify="end">
            <Col>{dayjs(details.updatedAt).format("MMMM D, YYYY")}</Col>
          </Row>
        </Col>
        <Col span={24} className="m-b-20">
          <Image
            className="info_img"
            style={{ display: details.coverImage ? "block" : "none" }}
            src={details.coverImage}
            fallback="data:image/png;base64,"
          />
        </Col>
        <Col span={24}>
          <div dangerouslySetInnerHTML={{ __html: details.content }}></div>
        </Col>
        <Col span={24} className="m-b-20">
          <div
            style={{
              display: details.source ? "block" : "none",
              marginTop: "20px",
            }}
          >
            <a
              style={{
                display:details.source!=='N/A'&&params.type === "article"?"block" : "none",
              }}
              href={details.source}
              target="_blank"
            >
              Article source
            </a>
          </div>
        </Col>
      </Row>
    </div>
  )
}

export default Details
